const HOST = 'localhost';
const USER = 'root';
const PASSWORD = '';
const DATABASE = 'news_web';
const TABLE = 'news';
const mysql = require('mysql2/promise');

function getSQLResult(sqlQuery) {
  return new Promise(async (resolve, reject) => {
    try {
      const connection = mysql.createPool({
        host: HOST,
        user: USER,
        password: PASSWORD,
        database: DATABASE,
      });

      const [rows] = await connection.execute(sqlQuery);
      await connection.end();

      resolve(JSON.stringify(rows));
    } catch (e) {
      console.error('Database Error:', e);
      reject(e);
    }
  });
}

// Fungsi untuk membangun query berdasarkan intent chatbot
function buildQuery(intent, params = {}) {
  let query = '';

  switch(intent) {
    case 'get_latest_news':
      query = `SELECT id, title, subtitle, content, publisher, category, jumlah_view, published_at, published_time FROM ${TABLE} ORDER BY published_at DESC, published_time DESC LIMIT ${params.limit || 5}`;
      break;

    case 'get_popular_news':
      query = `SELECT id, title, subtitle, content, publisher, category, jumlah_view, published_at, published_time FROM ${TABLE} ORDER BY jumlah_view DESC LIMIT ${params.limit || 5}`;
      break;

    case 'search_news':
      query = `SELECT id, title, subtitle, content, publisher, category, jumlah_view, published_at, published_time FROM ${TABLE} WHERE title LIKE '%${params.keyword}%' OR content LIKE '%${params.keyword}%' OR subtitle LIKE '%${params.keyword}%' ORDER BY published_at DESC LIMIT ${params.limit || 10}`;
      break;

    case 'get_news_by_category':
      query = `SELECT id, title, subtitle, content, publisher, category, jumlah_view, published_at, published_time FROM ${TABLE} WHERE category = '${params.category}' ORDER BY published_at DESC LIMIT ${params.limit || 10}`;
      break;

    case 'get_news_by_id':
      query = `SELECT id, title, subtitle, content, publisher, category, jumlah_view, published_at, published_time FROM ${TABLE} WHERE id = ${params.id}`;
      break;

    case 'get_news_by_publisher':
      query = `SELECT id, title, subtitle, content, publisher, category, jumlah_view, published_at, published_time FROM ${TABLE} WHERE publisher LIKE '%${params.publisher}%' ORDER BY published_at DESC LIMIT ${params.limit || 10}`;
      break;

    case 'get_news_stats':
      query = `SELECT COUNT(*) as total_news, AVG(jumlah_view) as avg_views, MAX(jumlah_view) as max_views, COUNT(DISTINCT category) as total_categories FROM ${TABLE}`;
      break;

    case 'get_categories':
      query = `SELECT DISTINCT category, COUNT(*) as count FROM ${TABLE} GROUP BY category ORDER BY count DESC`;
      break;

    default:
      query = `SELECT id, title, subtitle, content, publisher, category, jumlah_view, published_at, published_time FROM ${TABLE} ORDER BY published_at DESC, published_time DESC LIMIT 5`;
  }

  return query;
}

// Fungsi untuk format response dengan link
function formatNewsResponse(newsData) {
  try {
    const news = JSON.parse(newsData);

    if (!news || news.length === 0) {
      return JSON.stringify({ 
        status: 'no_data', 
        message: 'Tidak ada berita ditemukan' 
      });
    }

    const formattedNews = news.map(item => {
      // Format tanggal dan waktu yang lebih readable
      const publishedDate = new Date(item.published_at).toLocaleDateString('id-ID', {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });

      return {
        ...item,
        link: `http://127.0.0.1:8000/viewNews/${item.id}`,
        formatted_link: `[${item.title}](http://127.0.0.1:8000/viewNews/${item.id})`,
        formatted_date: publishedDate,
        content_preview: item.content ? item.content.substring(0, 200) + '...' : ''
      };
    });

    return JSON.stringify({
      status: 'success',
      data: formattedNews,
      count: formattedNews.length
    });

  } catch (error) {
    return newsData; // Return original jika gagal format
  }
}

// Fungsi utama untuk chatbot
async function handleChatbotQuery(intent, params = {}) {
  try {
    const sqlQuery = buildQuery(intent, params);
    console.log('Executing query:', sqlQuery);

    const result = await getSQLResult(sqlQuery);
    const formattedResult = formatNewsResponse(result);

    return formattedResult;
  } catch (error) {
    console.error('Chatbot query error:', error);
    return JSON.stringify({ 
      status: 'error',
      message: 'Terjadi kesalahan dalam mengambil data berita', 
      error: error.message 
    });
  }
}

